(function(){tinymce.PluginManager.requireLangPack('filemanager');tinymce.create('tinymce.plugins.FileManager',{init:function(ed,url){ed.addCommand('mceFilemanager',function(){admin.file_manager.tinymce.open()});ed.addButton('filemanager',{title:'Gestionnaire de fichier',cmd:'mceFilemanager',image:url+'/img/picture.png'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('filemanager',n.nodeName=='IMG')})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'Example plugin',author:'Some author',authorurl:'http://tinymce.moxiecode.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/example',version:"1.0"}}});tinymce.PluginManager.add('filemanager',tinymce.plugins.FileManager)})();